    private void btnMultiplicacionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMultiplicacionActionPerformed
    String multiplicacion = btnMultiplicacion.getText();
    if (lectura == "" || lectura.contains("*") || lectura.contains("+") || lectura.contains("-")||lectura.contains("/")){
        display.setText(lectura);
        }
    else { 
        lectura = lectura + multiplicacion;
        display.setText(lectura);
        }
    }//GEN-LAST:event_btnMultiplicacionActionPerformed

