    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed
    String igual = btnIgual.getText();
    if (lectura.contains("+")){
    
    String[] separarTodo = lectura.split("\\+");
    String nUno = separarTodo[0];
    String nDos = separarTodo[1];
    int numeroUno = Integer.parseInt(nUno);
    int numeroDos = Integer.parseInt(nDos);
    int resultado = numeroUno + numeroDos;
    String resultadoFinal = Integer.toString(resultado);
    display.setText(resultadoFinal);
    System.out.println(resultado);
        }
    else if (lectura.contains("-")){
    String[] separarTodo = lectura.split("\\-");
    String nUno = separarTodo[0];
    String nDos = separarTodo[1];
    int numeroUno = Integer.parseInt(nUno);
    int numeroDos = Integer.parseInt(nDos);
    int resultado = numeroUno - numeroDos;
    String resultadoFinal = Integer.toString(resultado);
    display.setText(resultadoFinal);
        }
    else if(lectura.contains("/")){
    String[] separarTodo = lectura.split("\\/");
    String nUno = separarTodo[0];
    String nDos = separarTodo[1];
    int numeroUno = Integer.parseInt(nUno);
    int numeroDos = Integer.parseInt(nDos);
    int resultado = numeroUno / numeroDos;
    String resultadoFinal = Integer.toString(resultado);
    display.setText(resultadoFinal);
        }
    else if (lectura.contains("*")){
            String[] separarTodo = lectura.split("\\*");
    String nUno = separarTodo[0];
    String nDos = separarTodo[1];
    int numeroUno = Integer.parseInt(nUno);
    int numeroDos = Integer.parseInt(nDos);
    int resultado = numeroUno * numeroDos;
    String resultadoFinal = Integer.toString(resultado);
    display.setText(resultadoFinal);
        }
    else {
        display.setText("");
        }
    }//GEN-LAST:event_btnIgualActionPerformed



